CREATE OR REPLACE PACKAGE PKG_SHAPE_CALCULATION
IS
    c_pi CONSTANT NUMBER := acos(-1);
    
    FUNCTION RECTANGLE_AREA(
        p_length NUMBER,
        p_width NUMBER)
    RETURN NUMBER;
    
    FUNCTION RECTANGLE_AREA(
        p_length NUMBER)
    RETURN NUMBER;
    
    FUNCTION CIRCLE_AREA(
        p_radius NUMBER)
    RETURN NUMBER;
    
END PKG_SHAPE_CALCULATION;
/

CREATE OR REPLACE PACKAGE BODY PKG_SHAPE_CALCULATION
IS
    FUNCTION CHECK_VALUE(
        p_value NUMBER)
    RETURN boolean
    IS
        v_boolean boolean;
BEGIN
    IF p_value > 0 THEN
    v_boolean := TRUE;
    ELSE
    v_boolean := FALSE;
END IF;
    RETURN v_boolean;
    END CHECK_VALUE;


-- Question 3a
FUNCTION RECTANGLE_AREA
    (p_length NUMBER,
    p_width NUMBER)
    RETURN NUMBER
IS
    p_area NUMBER;
BEGIN

IF CHECK_VALUE (p_length) = TRUE
    AND CHECK_VALUE (p_width) = TRUE THEN
    p_area := p_width*p_length;
ELSE
    p_area := -1;
    dbms_output.put_line('Length and width has to be positive');
END IF;
RETURN p_area;

EXCEPTION
    WHEN OTHERS THEN
        dbms_output.put(SQLCODE);
        dbms_output.put(': ');
        dbms_output.put_line(substr(SQLERRM, 1, 1000));
        
END RECTANGLE_AREA;

