-- Question 3d
FUNCTION CIRCLE_AREA
    (p_radius NUMBER)
    RETURN NUMBER
IS
    p_area NUMBER;
    
BEGIN
    IF CHECK_VALUE(p_radius) = TRUE THEN
    p_area := c_pi * p_radius * p_radius;
ELSE
p_area := -1;
dbms_output.put_line('Radius has to be positive');

END IF;
RETURN p_area;

EXCEPTION
    WHEN OTHERS THEN
        dbms_output.put(SQLCODE);
        dbms_output.put(': ');
        dbms_output.put_line(substr(SQLERRM, 1, 1000));
END CIRCLE_AREA;

END PKG_SHAPE_CALCULATION;
